try {
    const sound = await fromUuid("Playlist.TTlMcjeZdPFXaBvw.PlaylistSound.3JD3E8I4rWlAggzW");
    if (sound) {
        if (sound instanceof Playlist) {
            sound.playAll();
        } else {
            if (!sound.playing)
                sound.parent.playSound(sound);
            else
                sound.update({ playing: false, pausedTime: sound.sound.currentTime });
        }
    }
} catch {}
