try {
    const sound = await fromUuid("Playlist.soZJNZX6s1AM0SrI.PlaylistSound.QkY9YKGIWpMFYSmx");
    if (sound) {
        if (sound instanceof Playlist) {
            sound.playAll();
        } else {
            if (!sound.playing)
                sound.parent.playSound(sound);
            else
                sound.update({ playing: false, pausedTime: sound.sound.currentTime });
        }
    }
} catch {}
