macros.command:
//*Narrator's Toolbox - Edit Protagonist Details*//
//? This macro allows the Narrator to edit a Protagonist's Details
//? If you'd like to edit another Actor type, change the "Protagonist" in the filter on line 8
if (!game.user.isGM) {
	ui.notifications.warn("You must be a Narrator to use this macro.");
	return;
}
let actors = game.actors.contents.filter((a) => a.type === "Protagonist");
let dialogContent = `<form>
<div class="style-form">To be used to edit a Protagonists's Name, Player, Gender, Life, Experience and Destiny<br><br></div>
<div class="style-form">Note that 'Player Name' needs to be the same as under Settings - 'User Management', for the buttons to appear in the Chat for that player. You can also use the 'Assign Player' on an actor to pass control to a Player.<br><br></div>
<div class="form-group style-form">
	<div class="style-form">Protagonist Name</div>
	<div class="style-form">New Protagonist Name</div>
	<div class="style-form">New Player Name</div>
	<div class="style-form">New Gender</div>
	<div class="style-form">New Life Current</div>
	<div class="style-form">New Total Experience</div>
	<div class="style-form">New Current Destiny</div>
</div>
`;
for (let actor of actors) {
	dialogContent += `<div class="form-group">
			<div class="style-form">${actor.name}</div>
			<div class="style-form"><input type="text" name="newName-${actor.id}" value="${actor.name}"></div>
			<div class="style-form"><input type="text" name="newPlayer-${actor.id}" value="${actor.system.metaowner.value}"></div>
			<div class="style-form"><input type="text" name="newGender-${actor.id}" value="${actor.system.humanoids.gender.value}"></div>
			<div class="style-form"><input type="number" dtype="Number" name="life-${actor.id}" value="${actor.system.Vital.Life.value}"></div>
			<div class="style-form"><input type="number" name="totalExperience-${actor.id}" value="${actor.system.Vital.Experience.Total}"></div>
			<div class="style-form"><input type="number" name="currentDestiny-${actor.id}" value="${actor.system.Vital.Destiny.value}"></div>
		</div>`;
}
dialogContent += `</form><br><br>`;
dialogContent += `<div>Confirming will make the new Current Destiny also the Max Destiny for each Protagonist</div><br><br>`;
let dialogOptions = {
	width: 1000,
	height: 520,
	resizable: true,
};
let toolboxdialog = new Dialog(
	{
		title: "Narrator's Toolbox - Edit Protagonist Details",
		content: dialogContent,
		buttons: {
			ok: {
				label: "Confirm",
				callback: async (html) => {
					for (let actor of actors) {
						let totalExperience = html.find(`[name="totalExperience-${actor.id}"]`).val();
						let currentDestiny = html.find(`[name="currentDestiny-${actor.id}"]`).val();
						let maxDestiny = currentDestiny;
						let newName = html.find(`[name="newName-${actor.id}"]`).val();
						let newPlayer = html.find(`[name="newPlayer-${actor.id}"]`).val();
						let newGender = html.find(`[name="newGender-${actor.id}"]`).val();
						let life = html.find(`[name="life-${actor.id}"]`).val();
						await actor.update({
							name: newName,
							"system.metaowner.value": newPlayer,
							"system.humanoids.gender.value": newGender,
							"system.Vital.Experience.Total": totalExperience,
							"system.Vital.Destiny.value": currentDestiny,
							"system.Vital.Destiny.max": maxDestiny,
							"prototypeToken.name": newName,
							"system.Vital.Life.value": life,
						});
					}
				},
			},
			cancel: {
				label: "Cancel",
			},
		},
		default: "ok",
	},
	dialogOptions
);
toolboxdialog.render(true);

//*Narrator's Toolbox - Edit Protagonist Stats*//
//? This macro allows the Narrator to edit a Protagonist's Stats
//? If you'd like to edit another Actor type, change the "Protagonist" in the filter on line 8
if (!game.user.isGM) {
	ui.notifications.warn("You must be a Narrator to use this macro.");
	return;
}
let actors = game.actors.contents.filter((a) => a.type === "Protagonist");
let dialogContent = `<form>
<div class="style-form">To be used to edit a Protagonists's Initial Stat Scores, and Initial Characteristic Scores to custom values<br><br></div>`;
dialogContent += `<div class="form-group style-form">
<div class="style-form">Protagonist Name</div>
<div class="style-form">BODY</div>
<div class="style-form">Endurance</div>
<div class="style-form">Power</div>
<div class="style-form">Reflexes</div>
<div class="style-form">MIND</div>
<div class="style-form">Creativity</div>
<div class="style-form">Manipulation</div>
<div class="style-form">Perception</div>
<div class="style-form">SOUL</div>
<div class="style-form">Awareness</div>
<div class="style-form">Consciousness</div>
<div class="style-form">Willpower</div>
</div>
`;
for (let actor of actors) {
	dialogContent += `<div class="form-group">
		<div class="style-form">${actor.name}</div>
		<div class="style-form"><input type="number" name="newBody-${actor.id}" value="${actor.system.Characteristics.Body.Initial}"></div>
		<div class="style-form"><input type="number" name="newEndurance-${actor.id}" value="${actor.system.Characteristics.Body.Stats.Endurance.Initial}"></div>
		<div class="style-form"><input type="number" name="newPower-${actor.id}" value="${actor.system.Characteristics.Body.Stats.Power.Initial}"></div>
		<div class="style-form"><input type="number" name="newReflexes-${actor.id}" value="${actor.system.Characteristics.Body.Stats.Reflexes.Initial}"></div>
		<div class="style-form"><input type="number" name="newMind-${actor.id}" value="${actor.system.Characteristics.Mind.Initial}"></div>
		<div class="style-form"><input type="number" name="newCreativity-${actor.id}" value="${actor.system.Characteristics.Mind.Stats.Creativity.Initial}"></div>
		<div class="style-form"><input type="number" name="newManipulation-${actor.id}" value="${actor.system.Characteristics.Mind.Stats.Manipulation.Initial}"></div>
		<div class="style-form"><input type="number" name="newPerception-${actor.id}" value="${actor.system.Characteristics.Mind.Stats.Perception.Initial}"></div>
		<div class="style-form"><input type="number" name="newSoul-${actor.id}" value="${actor.system.Characteristics.Soul.Initial}"></div>
		<div class="style-form"><input type="number" name="newAwareness-${actor.id}" value="${actor.system.Characteristics.Soul.Stats.Awareness.Initial}"></div>
		<div class="style-form"><input type="number" name="newConsciousness-${actor.id}" value="${actor.system.Characteristics.Soul.Stats.Consciousness.Initial}"></div>
		<div class="style-form"><input type="number" name="newWillpower-${actor.id}" value="${actor.system.Characteristics.Soul.Stats.Willpower.Initial}"></div>
		</div>`;
}
dialogContent += `</form><br><br>`;
dialogContent += `<div>Confirming will set the Protagonists' Current Life to their Max Life</div><br><br>`;
let dialogOptions = {
	width: 1250,
	height: 720,
	resizable: true,
};
let toolboxdialog = new Dialog(
	{
		title: "Narrator's Toolbox - Edit Protagonist Stats",
		content: dialogContent,
		buttons: {
			ok: {
				label: "Confirm",
				callback: async (html) => {
					for (let actor of actors) {
						let newBody = parseInt(html.find(`[name="newBody-${actor.id}"]`).val());
						let newEndurance = parseInt(html.find(`[name="newEndurance-${actor.id}"]`).val());
						let newPower = parseInt(html.find(`[name="newPower-${actor.id}"]`).val());
						let newReflexes = parseInt(html.find(`[name="newReflexes-${actor.id}"]`).val());
						let newMind = parseInt(html.find(`[name="newMind-${actor.id}"]`).val());
						let newCreativity = parseInt(html.find(`[name="newCreativity-${actor.id}"]`).val());
						let newManipulation = parseInt(html.find(`[name="newManipulation-${actor.id}"]`).val());
						let newPerception = parseInt(html.find(`[name="newPerception-${actor.id}"]`).val());
						let newSoul = parseInt(html.find(`[name="newSoul-${actor.id}"]`).val());
						let newAwareness = parseInt(html.find(`[name="newAwareness-${actor.id}"]`).val());
						let newConsciousness = parseInt(html.find(`[name="newConsciousness-${actor.id}"]`).val());
						let newWillpower = parseInt(html.find(`[name="newWillpower-${actor.id}"]`).val());
						await actor.update({
							"system.Characteristics.Body.Initial": newBody,
							"system.Characteristics.Body.Stats.Endurance.Initial": newEndurance,
							"system.Characteristics.Body.Stats.Power.Initial": newPower,
							"system.Characteristics.Body.Stats.Reflexes.Initial": newReflexes,
							"system.Characteristics.Mind.Initial": newMind,
							"system.Characteristics.Mind.Stats.Creativity.Initial": newCreativity,
							"system.Characteristics.Mind.Stats.Manipulation.Initial": newManipulation,
							"system.Characteristics.Mind.Stats.Perception.Initial": newPerception,
							"system.Characteristics.Soul.Initial": newSoul,
							"system.Characteristics.Soul.Stats.Awareness.Initial": newAwareness,
							"system.Characteristics.Soul.Stats.Consciousness.Initial": newConsciousness,
							"system.Characteristics.Soul.Stats.Willpower.Initial": newWillpower,
						});
						//? Set Current Life = Max Life
						let maxlife = await actor.system.Vital.Life.max;
						await actor.update({ "system.Vital.Life.value": Number(maxlife) });
					}
				},
			},
			cancel: {
				label: "Cancel",
			},
		},
		default: "ok",
	},
	dialogOptions
);
toolboxdialog.render(true);

//*Narrator's Toolbox - End of Session*//
if (!game.user.isGM) {
	ui.notifications.warn("You must be a Narrator to use this macro.");
	return;
}
let actors = game.actors.contents.filter((a) => a.type === "Protagonist");
let assignedActors = actors.filter((actor) => actor.system.metaowner.value !== "The Composer");
let arcTitle = actors[0]?.system.Vital.arc.title || "";
let regressionTitle = actors[0]?.system.entermeta.regression.title || "";
let dialogContent = `<form>
<div class="style-form">To be used at the end of each Session to award EXP.<br><br></div>
<div class="style-form">Award a number of tally marks (typically up to 5) for each category below and you may also give Bonus EXP to each Protagonist.</div>
<div class="style-form">Please reffer to the <a title="Requires Narrator access on Metanthropes.com" href="https://www.metanthropes.com/techniques/">Narrator's Techniques</a> on how to award EXP. An in-game Journal will be added at a later time.<br></div>
<div class="form-group style-form">EXP awarded for each tally mark:  <input type="number" data-dtype="Number" name="tallyEXP" value="100"><br><br></div>
<div class="form-group style-form">
	<div class="style-form">Protagonist</div>
	<div class="style-form">Sessions Played</div>
	<div class="style-form" title="${arcTitle}">Arc</div>
	<div class="style-form" title="${regressionTitle}">Regression</div>
	<div class="style-form">Current EXP</div>
	<div class="style-form" title="Roleplay rewards Players for authentically embodying the essence of their Protagonists and living out their lives.">Roleplay</div>
	<div class="style-form" title="Acting is awarded for the effort Players put into bringing their Protagonists to life through performance.">Acting</div>
	<div class="style-form" title="Goals rewards Players for setting and advancing towards significant objectives that align with the broader narrative of the Saga.">Goals</div>
	<div class="style-form" title="Adversity is awarded to Players when their Protagonists engage in dangerous situations, when they face their fears, overcome adversity, and overpower their adversaries.">Adversity</div>
	<div class="style-form" title="Influence should be given to Players whose actions and roleplay instigate change by affecting the lives of NPCs.">Influence</div>
	<div class="style-form" title="Discovery is awarded when Protagonists learn more about the world they inhabit, explore new lands, cultures, and ideas, or when they encounter new people or discover new clues.">Discovery</div>
	<div class="style-form" title="Award this number of bonus EXP to the Protagonist.">Bonus EXP</div>
</div>	
`;
for (let actor of assignedActors) {
	let regressionValue = actor.system.entermeta.regression.value;
	let regressionTitle =
		actor.system.entermeta.regression.options[regressionValue]?.title ||
		"This is not an officially supported Regression";
	let arcValue = actor.system.Vital.arc.value;
	let arcTitle = actor.system.Vital.arc.options[arcValue]?.title || "This is not an officially supported Arc";
	dialogContent += `<div class="form-group">
			<div class="style-form">${actor.name}</div>
			<div class="style-form"><align=center>${actor.system.entermeta.sessions.value}</align></div>
			<div class="style-form" title="${arcTitle}">${arcValue}</div>
			<div class="style-form" title="${regressionTitle}">${regressionValue}</div>
			<div class="style-form">${actor.system.Vital.Experience.Total}</div>
			<div class="style-form"><input type="number" name="roleplayTallyMarks-${actor.id}" value="0"></div>
			<div class="style-form"><input type="number" name="actingTallyMarks-${actor.id}" value="0"></div>
			<div class="style-form"><input type="number" name="goalsTallyMarks-${actor.id}" value="0"></div>
			<div class="style-form"><input type="number" name="adversityTallyMarks-${actor.id}" value="0"></div>
			<div class="style-form"><input type="number" name="influenceTallyMarks-${actor.id}" value="0"></div>
			<div class="style-form"><input type="number" name="discoveryTallyMarks-${actor.id}" value="0"></div>
			<div class="style-form"><input type="number" name="bonusEXP-${actor.id}" value="0"></div>
		</div>`;
}
dialogContent += `</form><br><br>`;
dialogContent += `<div class="style-form">Confirming will also increase the Sessions Played by 1, for each Protagonist on this list.</div><br><br>`;
let dialogOptions = {
	width: 1500,
	height: 520,
	resizable: true,
};
let toolboxdialog = new Dialog(
	{
		title: "Narrator's Toolbox - End of Session",
		content: dialogContent,
		buttons: {
			ok: {
				label: "Confirm",
				callback: async (html) => {
					for (let actor of assignedActors) {
						let currentEXP = parseInt(actor.system.Vital.Experience.Total);
						let tallyEXP = parseInt(html.find(`[name="tallyEXP"]`).val());
						let roleplayEXP =
						parseInt(html.find(`[name="roleplayTallyMarks-${actor.id}"]`).val()) * tallyEXP;
						let actingEXP = parseInt(html.find(`[name="actingTallyMarks-${actor.id}"]`).val()) * tallyEXP;
						let goalsEXP = parseInt(html.find(`[name="goalsTallyMarks-${actor.id}"]`).val()) * tallyEXP;
						let adversityEXP =
						parseInt(html.find(`[name="adversityTallyMarks-${actor.id}"]`).val()) * tallyEXP;
						let influenceEXP =
						parseInt(html.find(`[name="influenceTallyMarks-${actor.id}"]`).val()) * tallyEXP;
						let discoveryEXP =
						parseInt(html.find(`[name="discoveryTallyMarks-${actor.id}"]`).val()) * tallyEXP;
						let bonusEXP = parseInt(html.find(`[name="bonusEXP-${actor.id}"]`).val());
						let newExperience =
							currentEXP +
							roleplayEXP +
							actingEXP +
							goalsEXP +
							adversityEXP +
							influenceEXP +
							discoveryEXP +
							bonusEXP;
						let sessionsPlayed = parseInt(actor.system.entermeta.sessions.value);
						let newSessionsPlayed = sessionsPlayed + 1;
						await actor.update({
							"system.Vital.Experience.Total": newExperience,
							"system.entermeta.sessions.value": newSessionsPlayed,
						});
					}
				},
			},
			cancel: {
				label: "Cancel",
			},
		},
		default: "ok",
	},
	dialogOptions
);
toolboxdialog.render(true);

//*Narrator's Toolbox - End of Scene*//
if (!game.user.isGM) {
	ui.notifications.warn("You must be a Narrator to use this Macro.");
	return;
}
let actors = game.actors.contents.filter((a) => a.type === "Protagonist");
//todo: instead of just checking for our Premade characters that come with 'The Composer' we should also exclude null values? or even better only actors that are controlled by active players instead?
let assignedActors = actors.filter((actor) => actor.system.metaowner.value !== "The Composer");
let arcTitle = actors[0]?.system.Vital.arc.title || "";
let regressionTitle = actors[0]?.system.entermeta.regression.title || "";
let dialogContent = `<form>
<div class="style-form">To be used at the end of each Scene to award Destiny</div>
<div class="style-form"><br>Only Protagonists with Assigned Players will be shown in the list below</div>
<div class="style-form">You can mouse-over their Arc & Regression, for a quick reminder<br><br></div>
<hr/>
<div class="style-form">Confirming will award Destiny and set Max Destiny accordingly for each Protagonist</div>
<div class="style-form">Protagonists with Luck Bending will be automatically awarded the extra Destiny</div><br><br>
<div class="form-group style-form">
	<div class="style-form">Protagonist</div>
	<div class="style-form" title="${arcTitle}">Arc</div>
	<div class="style-form" title="${regressionTitle}">Regression</div>
	<div class="style-form">Award Destiny</div>
</div>
`;
for (let actor of assignedActors) {
	let regressionValue = actor.system.entermeta.regression.value;
	let regressionTitle =
		actor.system.entermeta.regression.options[regressionValue]?.title ||
		"This is not an officially supported Regression";
	let arcValue = actor.system.Vital.arc.value;
	let arcTitle = actor.system.Vital.arc.options[arcValue]?.title || "This is not an officially supported Arc";
	dialogContent += `<div class="form-group style-form">
			<div class="style-form">${actor.name}</div>
			<div class="style-form" title="${arcTitle}">${arcValue}</div>
			<div class="style-form" title="${regressionTitle}">${regressionValue}</div>
			<div class="style-form"><input type="number" name="awardDestiny-${actor.id}" value="1"></div>
			</div>`;
}
dialogContent += `</form><br><br>`;
let dialogOptions = {
	width: 750,
	height: 380,
	resizable: true,
};
let toolboxdialog = new Dialog(
	{
		title: "Narrator's Toolbox - End of Scene",
		content: dialogContent,
		buttons: {
			ok: {
				label: "Confirm",
				callback: async (html) => {
					for (let actor of assignedActors) {
						const currentDestiny = parseInt(actor.system.Vital.Destiny.value);
						const awardDestiny = parseInt(html.find(`[name="awardDestiny-${actor.id}"]`).val());
						//? Check for Luck Bending that would affect Destiny awards
						let extraDestiny = 0;
						const equippedItems = actor.items.toObject();
						const equippedMetapowers = equippedItems.filter((item) => item.type === "Metapower");
						const equippedLuck = equippedMetapowers.filter(
							(item) => item.system.MetapowerName.value === "Luck Bending"
						);
						if (equippedLuck.length > 0) {
							const levels = equippedLuck.map((item) => item.system.Level.value);
							const maxLevel = Math.max(...levels);
							extraDestiny = maxLevel;
						}
						const newDestiny = currentDestiny + awardDestiny + extraDestiny;
						await actor.update({
							"system.Vital.Destiny.value": newDestiny,
							"system.Vital.Destiny.max": newDestiny,
						});
					}
				},
			},
			cancel: {
				label: "Cancel",
			},
		},
		default: "ok",
	},
	dialogOptions
);
toolboxdialog.render(true);

//*Narrator's Toolbox - End of Act*//
if (!game.user.isGM) {
	ui.notifications.warn("You must be a Narrator to use this macro.");
	return;
}
let actors = game.actors.contents.filter((a) => a.type === "Protagonist");
let dialogContent = `<form>
<div class="style-form">To be used at the end of each Act, to reward free Perks and to change Arc & Regression<br><br></div>
<div class="form-group">
	<div>Protagonist</div>
	<div>Arc</div>
	<div>New Arc</div>
	<div>Regression</div>
	<div>New Regression</div>
	<div>Starting/Free Perks</div>
	<div>Award Perks</div>
</div>`;
for (let actor of actors) {
	dialogContent += `<div class="form-group">
	<label>${actor.name}</label>
	<div>${actor.system.Vital.arc.value}</div>
	<div><input type="text" name="newArc-${actor.id}" value="${actor.system.Vital.arc.value}"></div>
	<div>${actor.system.entermeta.regression.value}</div>
	<div><input type="text" name="newRegression-${actor.id}" value="${actor.system.entermeta.regression.value}"></div>
	<div>${actor.system.Perks.Details.Starting.value}</div>
	<div><input type="number" name="awardPerks-${actor.id}" value="0"></div>
	</div>`;
}
dialogContent += `</form><br><br>`;
dialogContent += `<div>Awarding Perks will be considered as Starting/Free Perks and won't require spending Experience<br><br></div>`;
let dialogOptions = {
	width: 750,
	height: 520,
	resizable: true,
};
let toolboxdialog = new Dialog(
	{
		title: "Narrator's Toolbox - End of Act",
		content: dialogContent,
		buttons: {
			ok: {
				label: "Confirm",
				callback: async (html) => {
					for (let actor of actors) {
						let freePerks = parseInt(actor.system.Perks.Details.Starting.value);
						let awardPerks = parseInt(html.find(`[name="awardPerks-${actor.id}"]`).val());
						let newFreePerks = freePerks + awardPerks;
						let newArc = html.find(`[name="newArc-${actor.id}"]`).val();
						let newRegression = html.find(`[name="newRegression-${actor.id}"]`).val();
						await actor.update({
							"system.Perks.Details.Starting.value": newFreePerks,
							"system.Vital.arc.value": newArc,
							"system.entermeta.regression.value": newRegression,
						});
					}
				},
			},
			cancel: {
				label: "Cancel",
			},
		},
		default: "ok",
	},
	dialogOptions
);
toolboxdialog.render(true);
